package com.syntax.class01;

public class PrintAndPrintln {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.print("Hello");//THIS STATEMENT IS NOT REQUESTING NEW LINE SO (Line7 and Line8)will be in one LINE
System.out.println("My name is Masoud");//this statement is creating a new line for (line 9)
System.out.print("Nice to meet you classmate");
System.out.println("I'm excited");//(line 9 and Line10)both will be in same line since statement in line 
//9 didn't request new blank line

	}

}
