package com.syntax.class03;

public class MoreExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
 * if it is a break time -->I will get coffee otherwise I will focus on the class 
 */
		boolean isBreakTime=false;
		if(isBreakTime) {
			System.out.println("I'am getting my coffee");
		}else {
			System.out.println("I am focussing on the class ");
		}
		
		/*
		 * if you are hungry -->I will go eat otherwise:I will go have a tea
		 */
		
		boolean isHungry=false;
		if(isHungry) {
			System.out.println("I will go eat");
		}else {
			System.out.println("I will go have a tea");
		}
	
	}
}
	


