package com.syntax.class07;

public class WhyWeNeedLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		
if(i<4) {
	System.out.println("Hello");
}
System.out.println("===============Hello using while loop==========================");
while(i<=4) {
	System.out.println("Hello");
	i+=2;
}
	}

}
