package com.syntax.class09;

public class CarOdometer {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int r=0;r<=9;r++) {
			for(int c=0;c<=9;c++) {
				for(int j=0;j<=9;j++) {
					for(int k=0;k<=9;k++)
					System.out.println(r+""+c+":"+j+""+k);
				}
			}
		}
			

	}

}
