package com.syntax.notes;

public class ArithmeticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// we can use our arithmetic operator to add, subtract, diviede, multiply, and get the modulus
		// + sign works as addition when it sees numbers 
		//just like in the real world we still fallow presidence rule
		
		//example of presidency when it sees this formula it will first multiply then add
		int guess;
		guess=20+20*2; //60
		System.out.println(guess);
		//unless we put inside ( ) only then first will be what is inside parenthesis
		guess=(20+20)*2;//80
		System.out.println(guess);
		
		
	}

}
